import React, { useState } from "react";
import velogo from "../../Assets/velogo.jpg";

function JobForm() {
  const [formData, setFormData] = useState({
    Name: "",
    Email: "",
    NIC: "",
    Technician: "",
    Mobile: "",
    Repair_Description: "",
    Status: "Pending",
  });

  const [errors, setErrors] = useState({});
  const [success, setSuccess] = useState("");
  const [jobNo, setJobNo] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validation
  const validate = () => {
    const newErrors = {};
    if (!formData.Name.trim()) newErrors.Name = "Customer name is required";
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.Email))
      newErrors.Email = "Invalid email format";
    if (!/^\d{10,12}$/.test(formData.NIC))
      newErrors.NIC = "NIC must be 10‚Äì12 digits only";
    if (!formData.Technician.trim())
      newErrors.Technician = "Technician is required";
    if (!/^07\d{8}$/.test(formData.Mobile))
      newErrors.Mobile = "Mobile must start with 07 and contain 10 digits";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Submit form
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;
    try {
      const response = await fetch("http://localhost:5000/techs", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const result = await response.json();
      if (response.ok) {
        setSuccess("Job card created successfully!");
        setJobNo(result.Job_No);
        setFormData({
          Name: "",
          Email: "",
          NIC: "",
          Technician: "",
          Mobile: "",
          Repair_Description: "",
          Status: "Pending",
        });
        setErrors({});
      } else {
        alert(result.message || "Failed to create job card");
      }
    } catch (error) {
      console.error(error);
      alert("Error submitting form");
    }
  };

  const downloadPDF = async () => {
    if (!jobNo) return alert("No job created yet!");
    const response = await fetch(`http://localhost:5000/techs/${jobNo}/pdf`);
    if (!response.ok) return alert("Failed to download PDF");
    const blob = await response.blob();
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `JobCard_${jobNo}.pdf`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="min-h-screen py-10 bg-[#F8F9FA] font-sans">
      <div className="max-w-4xl mx-auto bg-white rounded-2xl shadow-xl overflow-hidden">
        {/* Header */}
        <div className="bg-[#0057B8] text-white text-center py-6 flex flex-col items-center">
          <img src={velogo} alt="Logo" className="w-20 mb-2" />
          <h2 className="text-2xl font-bold">Customer Job Card</h2>
          <p className="text-blue-100">
            Create and manage repair requests efficiently
          </p>
        </div>

        {/* Body */}
        <div className="p-8">
          {success && (
            <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 mb-6 rounded">
              ‚úÖ {success}
            </div>
          )}

          <form onSubmit={handleSubmit} className="grid md:grid-cols-2 gap-6">
            {/* Name */}
            <div>
              <label className="font-semibold">üë§ Customer Name</label>
              <input
                type="text"
                name="Name"
                value={formData.Name}
                onChange={handleChange}
                placeholder="Enter customer name"
                className={`w-full mt-1 border ${
                  errors.Name ? "border-red-500" : "border-gray-300"
                } rounded-lg p-2 focus:ring-2 focus:ring-[#0057B8]`}
              />
              {errors.Name && (
                <p className="text-red-500 text-sm">{errors.Name}</p>
              )}
            </div>

            {/* Email */}
            <div>
              <label className="font-semibold">‚úâÔ∏è Email Address</label>
              <input
                type="email"
                name="Email"
                value={formData.Email}
                onChange={handleChange}
                placeholder="Enter email address"
                className={`w-full mt-1 border ${
                  errors.Email ? "border-red-500" : "border-gray-300"
                } rounded-lg p-2 focus:ring-2 focus:ring-[#0057B8]`}
              />
              {errors.Email && (
                <p className="text-red-500 text-sm">{errors.Email}</p>
              )}
            </div>

            {/* NIC */}
            <div>
              <label className="font-semibold">üÜî National ID (NIC)</label>
              <input
                type="text"
                name="NIC"
                value={formData.NIC}
                onChange={handleChange}
                placeholder="Enter NIC number"
                className={`w-full mt-1 border ${
                  errors.NIC ? "border-red-500" : "border-gray-300"
                } rounded-lg p-2 focus:ring-2 focus:ring-[#0057B8]`}
              />
              {errors.NIC && (
                <p className="text-red-500 text-sm">{errors.NIC}</p>
              )}
            </div>

            {/* Mobile */}
            <div>
              <label className="font-semibold">üì± Mobile Number</label>
              <div className="flex mt-1">
                <span className="bg-gray-200 border border-r-0 border-gray-300 px-3 py-2 rounded-l-lg text-gray-700">
                  +94
                </span>
                <input
                  type="text"
                  name="Mobile"
                  value={formData.Mobile}
                  onChange={handleChange}
                  placeholder="07XXXXXXXX"
                  className={`w-full border ${
                    errors.Mobile ? "border-red-500" : "border-gray-300"
                  } rounded-r-lg p-2 focus:ring-2 focus:ring-[#0057B8]`}
                />
              </div>
              {errors.Mobile && (
                <p className="text-red-500 text-sm">{errors.Mobile}</p>
              )}
            </div>

            {/* Technician */}
            <div>
              <label className="font-semibold">üîß Assigned Technician</label>
              <input
                type="text"
                name="Technician"
                value={formData.Technician}
                onChange={handleChange}
                placeholder="Enter technician name"
                className={`w-full mt-1 border ${
                  errors.Technician ? "border-red-500" : "border-gray-300"
                } rounded-lg p-2 focus:ring-2 focus:ring-[#0057B8]`}
              />
              {errors.Technician && (
                <p className="text-red-500 text-sm">{errors.Technician}</p>
              )}
            </div>

            {/* Status */}
            <div>
              <label className="font-semibold">üè∑Ô∏è Current Status</label>
              <div className="mt-2">
                <span className="bg-[#FFA500] text-[#212529] px-3 py-1 rounded-md text-sm font-semibold">
                  {formData.Status}
                </span>
              </div>
            </div>

            {/* Repair Description */}
            <div className="md:col-span-2">
              <label className="font-semibold">üõ†Ô∏è Repair Description</label>
              <textarea
                name="Repair_Description"
                value={formData.Repair_Description}
                onChange={handleChange}
                rows="4"
                placeholder="Describe the repair work needed..."
                className="w-full mt-1 border border-gray-300 rounded-lg p-2 focus:ring-2 focus:ring-[#0057B8]"
              ></textarea>
            </div>

            {/* Submit */}
            <div className="md:col-span-2 flex justify-center">
              <button
                type="submit"
                className="bg-[#FFA500] text-[#212529] font-bold px-8 py-3 rounded-full hover:bg-[#ffb733] flex items-center gap-2"
              >
                <img src={velogo} alt="Logo" className="w-6 h-6" />
                Submit Job Card
              </button>
            </div>
          </form>

          {jobNo && (
            <div className="text-center mt-6">
              <button
                onClick={downloadPDF}
                className="bg-green-600 text-white px-6 py-2 rounded-full hover:bg-green-700"
              >
                üìÑ Download Job Card (PDF) - Job No {jobNo}
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default JobForm;
