import React, { useState } from "react";
import { Container, Form, Button, Card } from "react-bootstrap";

function CheckStatus() {
  const [formData, setFormData] = useState({ jobNo: "", nic: "" });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Checking status for:", formData);
    // Later you can fetch status from backend using jobNo or NIC
    alert("Feature coming soon! You entered: " + (formData.jobNo || formData.nic));
  };

  return (
    <div style={{ backgroundColor: "#F8F9FA", minHeight: "100vh", padding: "60px 0" }}>
      <Container style={{ maxWidth: "600px" }}>
        <Card
          style={{
            padding: "30px",
            borderRadius: "15px",
            boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
          }}
        >
          <h2
            className="text-center mb-4"
            style={{ color: "#0057B8", fontWeight: "700" }}
          >
            Check Repair Status
          </h2>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Job Number</Form.Label>
              <Form.Control
                type="text"
                name="jobNo"
                value={formData.jobNo}
                onChange={handleChange}
                placeholder="Enter your Job Number"
              />
            </Form.Group>

            <div className="text-center mb-3">— OR —</div>

            <Form.Group className="mb-4">
              <Form.Label>NIC Number</Form.Label>
              <Form.Control
                type="text"
                name="nic"
                value={formData.nic}
                onChange={handleChange}
                placeholder="Enter your NIC Number"
              />
            </Form.Group>

            <div className="text-center">
              <Button
                type="submit"
                style={{
                  backgroundColor: "#FFA500",
                  color: "#212529",
                  fontWeight: "600",
                  padding: "10px 30px",
                  borderRadius: "50px",
                  border: "none",
                }}
              >
                Check Status
              </Button>
            </div>
          </Form>
        </Card>
      </Container>
    </div>
  );
}

export default CheckStatus;
