import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

function JobForm() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    Name: "",
    Email: "",
    NIC: "",
    Technician: "",
    Mobile: "",
    Repair_Description: "",
    Status: "Pending",
  });

  const [errors, setErrors] = useState({});
  const [success, setSuccess] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const validate = () => {
    const newErrors = {};
    if (!formData.Name.trim()) newErrors.Name = "Customer name is required";
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.Email))
      newErrors.Email = "Invalid email format";
    if (!/^\d{10,12}$/.test(formData.NIC))
      newErrors.NIC = "NIC must be 10–12 digits only";
    if (!formData.Technician.trim())
      newErrors.Technician = "Technician is required";
    if (!/^07\d{8}$/.test(formData.Mobile))
      newErrors.Mobile = "Mobile must start with 07 and contain 10 digits";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    try {
      const response = await fetch("http://localhost:5000/techs", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const result = await response.json();

      if (response.ok) {
        setSuccess("Job card created successfully!");
        setFormData({
          Name: "",
          Email: "",
          NIC: "",
          Technician: "",
          Mobile: "",
          Repair_Description: "",
          Status: "Pending",
        });
        setErrors({});

        // Auto-redirect after 1.5 seconds
        setTimeout(() => navigate("/"), 1500);
      } else {
        alert(result.message || "Failed to create job card");
      }
    } catch (error) {
      console.error(error);
      alert("Error submitting form");
    }
  };

  // Used to color the status badge
  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case "completed":
        return "badge bg-success";
      case "pending":
      case "in-progress":
        return "badge bg-warning text-dark";
      case "failed":
        return "badge bg-danger";
      default:
        return "badge bg-secondary";
    }
  };

  return (
    <div className="min-vh-100 py-5" style={{ backgroundColor: "#F8F9FA" }}>
      <div className="container">
        <div className="card shadow-lg border-0 rounded-4">
          {/* Header */}
          <div
            className="card-header text-center text-white"
            style={{ backgroundColor: "#0057B8" }}
          >
            <h2 className="fw-bold mb-1">⚡ Customer Job Card</h2>
            <p className="mb-0" style={{ color: "#e0e0e0" }}>
              Create and manage repair requests efficiently
            </p>
          </div>

          {/* Body */}
          <div className="card-body p-4">
            {success && (
              <div className="alert alert-success d-flex align-items-center">
                <span className="me-2">✅</span>
                {success}
              </div>
            )}

            <form onSubmit={handleSubmit} className="row g-4">
              {/* Customer Name */}
              <div className="col-md-6">
                <label className="form-label fw-semibold">👤 Customer Name</label>
                <input
                  type="text"
                  name="Name"
                  value={formData.Name}
                  onChange={handleChange}
                  className={`form-control ${errors.Name ? "is-invalid" : ""}`}
                  placeholder="Enter customer name"
                />
                {errors.Name && <div className="invalid-feedback">{errors.Name}</div>}
              </div>

              {/* Email */}
              <div className="col-md-6">
                <label className="form-label fw-semibold">✉️ Email Address</label>
                <input
                  type="email"
                  name="Email"
                  value={formData.Email}
                  onChange={handleChange}
                  className={`form-control ${errors.Email ? "is-invalid" : ""}`}
                  placeholder="Enter email address"
                />
                {errors.Email && <div className="invalid-feedback">{errors.Email}</div>}
              </div>

              {/* NIC */}
              <div className="col-md-6">
                <label className="form-label fw-semibold">🆔 National ID (NIC)</label>
                <input
                  type="text"
                  name="NIC"
                  value={formData.NIC}
                  onChange={handleChange}
                  minLength="10"
                  maxLength="12"
                  className={`form-control ${errors.NIC ? "is-invalid" : ""}`}
                  placeholder="Enter NIC number"
                />
                {errors.NIC && <div className="invalid-feedback">{errors.NIC}</div>}
              </div>

              {/* Mobile */}
              <div className="col-md-6">
                <label className="form-label fw-semibold">📱 Mobile Number</label>
                <div className="input-group">
                  <span className="input-group-text">+94</span>
                  <input
                    type="text"
                    name="Mobile"
                    value={formData.Mobile}
                    onChange={handleChange}
                    minLength="10"
                    maxLength="10"
                    className={`form-control ${errors.Mobile ? "is-invalid" : ""}`}
                    placeholder="07XXXXXXXX"
                  />
                </div>
                {errors.Mobile && <div className="invalid-feedback">{errors.Mobile}</div>}
              </div>

              {/* Technician */}
              <div className="col-md-6">
                <label className="form-label fw-semibold">🔧 Assigned Technician</label>
                <input
                  type="text"
                  name="Technician"
                  value={formData.Technician}
                  onChange={handleChange}
                  className={`form-control ${errors.Technician ? "is-invalid" : ""}`}
                  placeholder="Enter technician name"
                />
                {errors.Technician && <div className="invalid-feedback">{errors.Technician}</div>}
              </div>

              {/* Status */}
              <div className="col-md-6">
                <label className="form-label fw-semibold">🏷️ Current Status</label>
                <div>
                  <span className={getStatusColor(formData.Status)}>
                    {formData.Status}
                  </span>
                </div>
              </div>

              {/* Repair Description */}
              <div className="col-12">
                <label className="form-label fw-semibold">🛠️ Repair Description</label>
                <textarea
                  name="Repair_Description"
                  value={formData.Repair_Description}
                  onChange={handleChange}
                  rows="4"
                  className="form-control"
                  placeholder="Describe the repair work needed..."
                />
              </div>

              {/* Submit Button */}
              <div className="col-12 text-center">
                <button
                  type="submit"
                  className="btn btn-lg px-5 fw-bold text-white"
                  style={{ backgroundColor: "#FFA500", border: "none" }}
                >
                  ⚡ Submit Job Card
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default JobForm;
