import { useState } from "react";
import {
  Home,
  Package,
  Wrench,
  Info,
  User,
  ShoppingCart,
  Bell,
  Menu,
  X,
  CheckCircle,
  Clock,
  AlertCircle
} from "lucide-react";

// UI Components
const Badge = ({ children, className = "" }) => {
  return (
    
    <span
      className={`inline-flex items-center rounded-full px-2 py-0.5 text-xs font-medium bg-electric-blue text-white ${className}`}
    >
      {children}
    </span>
  );
};

const Button = ({ children, variant = "solid", size = "md", className = "", ...props }) => {
  const base = "inline-flex items-center justify-center font-medium rounded-lg transition-colors focus:outline-none";

  const variants = {
    solid: "bg-honeycomb-orange text-tech-white hover:bg-orange-600",
    ghost: "bg-transparent hover:bg-light-gray text-dark-charcoal",
  };

  const sizes = {
    sm: "px-3 py-1 text-sm",
    md: "px-4 py-2 text-base",
    lg: "px-6 py-3 text-lg",
    icon: "p-2",
  };

  return (
    <button
      className={`${base} ${variants[variant]} ${sizes[size]} ${className}`}
      {...props}
    >
      {children}
    </button>
  );
};

const Card = ({ children, className = "" }) => {
  return (
    <div className={`rounded-xl bg-white shadow-card p-4 ${className}`}>
      {children}
    </div>
  );
};

// CustomerHeader Component
const CustomerHeader = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);

  const notifications = [
    { id: 1, type: "success", title: "Order Confirmed", message: "Your order #VE001 has been confirmed and will be delivered soon.", time: "2 min ago", icon: CheckCircle },
    { id: 2, type: "info", title: "Repair Update", message: "Your smartphone repair is in progress. Expected completion: Tomorrow", time: "1 hr ago", icon: Clock },
    { id: 3, type: "warning", title: "Payment Reminder", message: "Payment pending for invoice #INV-123. Due date: Today", time: "3 hrs ago", icon: AlertCircle },
  ];

  const navigationItems = [
    { id: "home", label: "Home", icon: Home, href: "/" },
    { id: "products", label: "Products", icon: Package, href: "/products" },
    { id: "repair", label: "Repair", icon: Wrench, href: "/repair" },
    { id: "about", label: "About Us", icon: Info, href: "/about" },
  ];

  return (
    <header className="fixed top-0 left-0 right-0 z-50 bg-tech-white/95 backdrop-blur border-b shadow-card">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-20">
          {/* Logo */}
          <div className="flex items-center space-x-2">
            <div className="h-10 w-10 bg-gradient-primary rounded-md flex items-center justify-center shadow-button">
              <span className="text-tech-white font-bold text-sm">V</span>
            </div>
            <div>
              <h1 className="font-bold text-2xl text-electric-blue">Vijaya Electronics</h1>
              <p className="text-xs text-slate-gray">Electronic device selling and repair center</p>
            </div>
          </div>

          {/* Desktop Navigation */}
          <nav className="hidden md:flex items-center space-x-6">
            {navigationItems.map((item) => {
              const Icon = item.icon;
              return (
                <Button
                  key={item.id}
                  variant="ghost"
                  className="flex items-center space-x-2 px-3 py-2 rounded-lg hover:bg-light-gray hover:text-electric-blue"
                >
                  <Icon className="h-5 w-5" />
                  <span className="font-medium">{item.label}</span>
                </Button>
              );
            })}
          </nav>

          {/* Action Items */}
          <div className="flex items-center space-x-2 relative">
            {/* Notifications */}
            <div className="relative">
              <Button
                variant="ghost"
                size="icon"
                className="relative hover:bg-light-gray text-slate-gray hover:text-electric-blue transition-colors"
                onClick={() => setShowNotifications(!showNotifications)}
              >
                <Bell className="h-5 w-5" />
                <Badge className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center p-0 text-xs bg-honeycomb-orange text-tech-white border-0">
                  {notifications.length}
                </Badge>
              </Button>

              {showNotifications && (
                <Card className="absolute right-0 top-12 w-80 bg-tech-white shadow-elegant border border-light-gray z-50">
                  <div className="p-4">
                    <h3 className="font-semibold text-dark-charcoal mb-3">Notifications</h3>
                    <div className="space-y-3 max-h-64 overflow-y-auto">
                      {notifications.map((notification) => {
                        const IconComponent = notification.icon;
                        return (
                          <div key={notification.id} className="flex items-start space-x-3 p-2 hover:bg-light-gray rounded-lg transition-colors">
                            <IconComponent
                              className={`h-5 w-5 mt-0.5 ${
                                notification.type === 'success' ? 'text-success-green' :
                                notification.type === 'warning' ? 'text-honeycomb-orange' :
                                'text-electric-blue'
                              }`}
                            />
                            <div className="flex-1">
                              <p className="font-medium text-sm text-dark-charcoal">{notification.title}</p>
                              <p className="text-xs text-slate-gray mt-1">{notification.message}</p>
                              <p className="text-xs text-slate-gray mt-1">{notification.time}</p>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  </div>
                </Card>
              )}
            </div>

            {/* Cart */}
            <Button variant="ghost" size="icon" className="relative hover:bg-light-gray text-slate-gray hover:text-electric-blue transition-colors">
              <ShoppingCart className="h-5 w-5" />
              <Badge className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center p-0 text-xs bg-honeycomb-orange text-tech-white border-0">2</Badge>
            </Button>

            {/* Profile */}
            <Button variant="ghost" size="icon" className="relative hover:bg-light-gray text-slate-gray hover:text-electric-blue transition-colors">
              <User className="h-5 w-5" />
            </Button>

            {/* Mobile Menu Toggle */}
            <Button variant="ghost" size="icon" className="md:hidden" onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}>
              {isMobileMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
            </Button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isMobileMenuOpen && (
          <div className="md:hidden py-4 border-t border-light-gray">
            <nav className="flex flex-col space-y-2">
              {navigationItems.map((item) => {
                const Icon = item.icon;
                return (
                  <Button
                    key={item.id}
                    variant="ghost"
                    className="flex items-center space-x-2 px-3 py-2 rounded-lg hover:bg-light-gray hover:text-electric-blue"
                  >
                    <Icon className="h-5 w-5" />
                    <span className="font-medium">{item.label}</span>
                  </Button>
                );
              })}
            </nav>
          </div>
        )}
      </div>
    </header>
  );
};

export default CustomerHeader;
