import React, { useState } from "react";
import { Container, Card, Form, Button, Alert } from "react-bootstrap";

function JobForm() {
  const [formData, setFormData] = useState({
    Name: "",
    Email: "",
    NIC: "",
    Technician: "",
    Mobile: "",
    Repair_Description: "",
  });

  const [errors, setErrors] = useState({});
  const [success, setSuccess] = useState("");

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validate before submit
  const validate = () => {
    let newErrors = {};

    if (!formData.Name.trim()) newErrors.Name = "Customer name is required";
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.Email))
      newErrors.Email = "Invalid email format";
    if (!/^\d{10,12}$/.test(formData.NIC))
      newErrors.NIC = "NIC must be 10â€“12 digits only";
    if (!formData.Technician.trim())
      newErrors.Technician = "Technician is required";
    if (!/^07\d{8}$/.test(formData.Mobile))
      newErrors.Mobile = "Mobile must start with 07 and contain 10 digits";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validate()) return;

    try {
      const response = await fetch("http://localhost:5000/techs", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const result = await response.json();
      if (response.ok) {
        setSuccess("Job card created successfully!");
        setFormData({
          Name: "",
          Email: "",
          NIC: "",
          Technician: "",
          Mobile: "",
          Repair_Description: "",
        });
        setErrors({});
      } else {
        alert(result.message || "Failed to create job card");
      }
    } catch (error) {
      console.error(error);
      alert("Error submitting form");
    }
  };

  return (
    <Container className="mt-5">
      <Card className="shadow">
        <Card.Body>
          <h2 className="text-center mb-4">Customer Job Card</h2>

          {success && <Alert variant="success">{success}</Alert>}

          <Form onSubmit={handleSubmit}>
            {/* Name */}
            <Form.Group className="mb-3" controlId="formName">
              <Form.Label>Customer Name</Form.Label>
              <Form.Control
                type="text"
                name="Name"
                value={formData.Name}
                onChange={handleChange}
                isInvalid={!!errors.Name}
                required
              />
              <Form.Control.Feedback type="invalid">
                {errors.Name}
              </Form.Control.Feedback>
            </Form.Group>

            {/* Email */}
            <Form.Group className="mb-3" controlId="formEmail">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="Email"
                value={formData.Email}
                onChange={handleChange}
                isInvalid={!!errors.Email}
                required
              />
              <Form.Control.Feedback type="invalid">
                {errors.Email}
              </Form.Control.Feedback>
            </Form.Group>

            {/* NIC */}
            <Form.Group className="mb-3" controlId="formNIC">
              <Form.Label>NIC</Form.Label>
              <Form.Control
                type="text"
                name="NIC"
                value={formData.NIC}
                onChange={handleChange}
                minLength="10"
                maxLength="12"
                isInvalid={!!errors.NIC}
                required
              />
              <Form.Control.Feedback type="invalid">
                {errors.NIC}
              </Form.Control.Feedback>
            </Form.Group>

            {/* Technician */}
            <Form.Group className="mb-3" controlId="formTechnician">
              <Form.Label>Technician Assigned</Form.Label>
              <Form.Control
                type="text"
                name="Technician"
                value={formData.Technician}
                onChange={handleChange}
                isInvalid={!!errors.Technician}
                required
              />
              <Form.Control.Feedback type="invalid">
                {errors.Technician}
              </Form.Control.Feedback>
            </Form.Group>

            {/* Mobile */}
            <Form.Group className="mb-3" controlId="formMobile">
              <Form.Label>Mobile Number</Form.Label>
              <Form.Control
                type="text"
                name="Mobile"
                value={formData.Mobile}
                onChange={handleChange}
                placeholder="07XXXXXXXX"
                minLength="10"
                maxLength="10"
                isInvalid={!!errors.Mobile}
                required
              />
              <Form.Control.Feedback type="invalid">
                {errors.Mobile}
              </Form.Control.Feedback>
            </Form.Group>

            {/* Repair Description */}
            <Form.Group className="mb-3" controlId="formDescription">
              <Form.Label>Repair Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="Repair_Description"
                value={formData.Repair_Description}
                onChange={handleChange}
              />
            </Form.Group>

            {/* Submit */}
            <Button type="submit" variant="primary" className="w-100">
              Submit Job Card
            </Button>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
}

export default JobForm;
