import React from "react";
import { useNavigate } from "react-router-dom";
import { Carousel, Button, Container } from "react-bootstrap";

// Import local images correctly
import mobileImg from "../../Assets/mobile.jpg";
import laptopImg from "../../Assets/laptop.jpg";
import speakerImg from "../../Assets/speaker.jpg";

function Landing() {
  const navigate = useNavigate();

  const handleBookRepair = () => {
    navigate("/jobform");
  };

  return (
    <div style={{ backgroundColor: "#F8F9FA", minHeight: "100vh", padding: "50px 0" }}>
      {/* Section title */}
      <Container className="text-center mb-5">
        <h2 style={{ color: "#0057B8", fontWeight: "700" }}>Our Repair Services</h2>
        <p style={{ color: "#6C757D" }}>We provide expert repairs for electronics</p>
      </Container>

      {/* Carousel */}
      <Container style={{ maxWidth: "900px" }}>
        <Carousel fade interval={3000} pause={false}>
          <Carousel.Item>
            <img
              className="d-block w-100 rounded"
              src={mobileImg} // Use imported variable
              alt="Mobile Repair"
              style={{ maxHeight: "400px", objectFit: "cover" }}
            />
            <Carousel.Caption style={{ backgroundColor: "rgba(0,87,184,0.6)", borderRadius: "8px" }}>
              <h5 style={{ color: "#fff" }}>Mobile Repairs</h5>
              <p style={{ color: "#fff" }}>Screen, battery, charging issues fixed quickly.</p>
            </Carousel.Caption>
          </Carousel.Item>

          <Carousel.Item>
            <img
              className="d-block w-100 rounded"
              src={laptopImg} // Use imported variable
              alt="Laptop Repair"
              style={{ maxHeight: "400px", objectFit: "cover" }}
            />
            <Carousel.Caption style={{ backgroundColor: "rgba(0,87,184,0.6)", borderRadius: "8px" }}>
              <h5 style={{ color: "#fff" }}>Laptop & Desktop Repairs</h5>
              <p style={{ color: "#fff" }}>Hardware/software troubleshooting for PCs & laptops.</p>
            </Carousel.Caption>
          </Carousel.Item>

          <Carousel.Item>
            <img
              className="d-block w-100 rounded"
              src={speakerImg} // Use imported variable
              alt="Speaker Repair"
              style={{ maxHeight: "400px", objectFit: "cover" }}
            />
            <Carousel.Caption style={{ backgroundColor: "rgba(0,87,184,0.6)", borderRadius: "8px" }}>
              <h5 style={{ color: "#fff" }}>Speaker Repairs</h5>
              <p style={{ color: "#fff" }}>Sound issues and wiring problems fixed professionally.</p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>

        {/* Book Repair button */}
        <div className="text-center mt-4">
          <Button
            onClick={handleBookRepair}
            style={{
              backgroundColor: "#FFA500",
              color: "#212529",
              fontWeight: "600",
              padding: "10px 30px",
              borderRadius: "50px",
              border: "none",
            }}
          >
            Book a Repair
          </Button>
        </div>
      </Container>
    </div>
  );
}

export default Landing;
