import React, { useState } from "react";
import {
  Container,
  Form,
  Button,
  Card,
  Alert,
  OverlayTrigger,
  Tooltip,
  Modal,
} from "react-bootstrap";
import { FaEdit, FaTrash } from "react-icons/fa";

function CheckStatus() {
  const [formData, setFormData] = useState({ jobNo: "", nic: "" });
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");

  // Edit modal state
  const [showEdit, setShowEdit] = useState(false);
  const [editData, setEditData] = useState({});

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setResult(null);

    try {
      const query = formData.jobNo
        ? `jobNo=${formData.jobNo}`
        : `nic=${formData.nic}`;

      const res = await fetch(`http://localhost:5000/techs/status?${query}`);
      const data = await res.json();

      if (!res.ok) {
        setError(data.message || "No job found");
      } else {
        setResult(data);
      }
    } catch (err) {
      console.error(err);
      setError("Server error. Please try again later.");
    }
  };

  // Open modal with current job data
  const handleEdit = () => {
    setEditData(result);
    setShowEdit(true);
  };

  const handleEditChange = (e) => {
    setEditData({ ...editData, [e.target.name]: e.target.value });
  };

  const saveEdit = async () => {
    try {
      const res = await fetch(`http://localhost:5000/techs/${result._id}`, {
        method: "PUT", // or PATCH depending on backend
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(editData),
      });

      if (res.ok) {
        const updated = await res.json();
        setResult(updated); // update local state
        setShowEdit(false);
        alert("Job updated successfully!");
      } else {
        alert("Failed to update job.");
      }
    } catch (error) {
      console.error(error);
      alert("Error updating job.");
    }
  };

  const handleDelete = async () => {
    if (!window.confirm("Are you sure you want to delete this job?")) return;
    try {
      const res = await fetch(`http://localhost:5000/techs/${result._id}`, {
        method: "DELETE",
      });
      if (res.ok) {
        alert("Job deleted successfully!");
        setResult(null);
      } else {
        alert("Failed to delete job.");
      }
    } catch (error) {
      console.error(error);
      alert("Error deleting job.");
    }
  };

  return (
    <div
      style={{ backgroundColor: "#F8F9FA", minHeight: "100vh", padding: "60px 0" }}
    >
      <Container style={{ maxWidth: "600px" }}>
        <Card
          style={{
            padding: "30px",
            borderRadius: "15px",
            boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
          }}
        >
          <h2
            className="text-center mb-4"
            style={{ color: "#0057B8", fontWeight: "700" }}
          >
            Check Repair Status
          </h2>

          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Job Number</Form.Label>
              <Form.Control
                type="text"
                name="jobNo"
                value={formData.jobNo}
                onChange={handleChange}
                placeholder="Enter your Job Number"
              />
            </Form.Group>

            <div className="text-center mb-3">— OR —</div>

            <Form.Group className="mb-4">
              <Form.Label>NIC Number</Form.Label>
              <Form.Control
                type="text"
                name="nic"
                value={formData.nic}
                onChange={handleChange}
                placeholder="Enter your NIC Number"
              />
            </Form.Group>

            <div className="text-center">
              <Button
                type="submit"
                style={{
                  backgroundColor: "#FFA500",
                  color: "#212529",
                  fontWeight: "600",
                  padding: "10px 30px",
                  borderRadius: "50px",
                  border: "none",
                }}
              >
                Check Status
              </Button>
            </div>
          </Form>

          {/* Results */}
          {error && (
            <Alert variant="danger" className="mt-4">
              {error}
            </Alert>
          )}
          {result && (
            <Card className="mt-4 p-3 border-0 shadow-sm">
              <div className="d-flex justify-content-between align-items-center">
                <h5>Job Details</h5>
                <div>
                  <OverlayTrigger placement="top" overlay={<Tooltip>Update</Tooltip>}>
                    <Button
                      variant="outline-primary"
                      size="sm"
                      className="me-2"
                      onClick={handleEdit}
                    >
                      <FaEdit />
                    </Button>
                  </OverlayTrigger>
                  <OverlayTrigger placement="top" overlay={<Tooltip>Delete</Tooltip>}>
                    <Button
                      variant="outline-danger"
                      size="sm"
                      onClick={handleDelete}
                    >
                      <FaTrash />
                    </Button>
                  </OverlayTrigger>
                </div>
              </div>
              <p>
                <strong>Name:</strong> {result.Name}
              </p>
              <p>
                <strong>Email:</strong> {result.Email}
              </p>
              <p>
                <strong>NIC:</strong> {result.NIC}
              </p>
              <p>
                <strong>Technician:</strong> {result.Technician}
              </p>
              <p>
                <strong>Mobile:</strong> {result.Mobile}
              </p>
              <p>
                <strong>Description:</strong> {result.Repair_Description}
              </p>
              <p>
                <strong>Status:</strong>
                <span className="badge bg-warning text-dark ms-2">
                  {result.status}
                </span>
              </p>
            </Card>
          )}
        </Card>
      </Container>

      {/* Edit Modal */}
      <Modal show={showEdit} onHide={() => setShowEdit(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Job</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control
                name="Name"
                value={editData.Name || ""}
                onChange={handleEditChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                name="Email"
                value={editData.Email || ""}
                onChange={handleEditChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Technician</Form.Label>
              <Form.Control
                name="Technician"
                value={editData.Technician || ""}
                onChange={handleEditChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Mobile</Form.Label>
              <Form.Control
                name="Mobile"
                value={editData.Mobile || ""}
                onChange={handleEditChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                name="Repair_Description"
                value={editData.Repair_Description || ""}
                onChange={handleEditChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Status</Form.Label>
              <Form.Control
                name="status"
                value={editData.status || ""}
                onChange={handleEditChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEdit(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={saveEdit}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default CheckStatus;
