import React from "react";
import { Link, useLocation } from "react-router-dom";
import velogo from "../../Assets/velogo.jpg";

function Navbar() {
  const location = useLocation();

  const isActive = (path) =>
    location.pathname === path
      ? "text-[#FFA500] border-b-2 border-[#FFA500]"
      : "text-white hover:text-[#FFA500]";

  return (
    <nav className="bg-gradient-to-r from-[#0057B8] via-[#0074D9] to-[#0057B8] shadow-lg">
      <div className="max-w-7xl mx-auto px-6 py-3 flex justify-between items-center">
        {/* Logo + Brand */}
        <div className="flex items-center gap-3">
          <img
            src={velogo}
            alt="Logo"
            className="w-10 h-10 rounded-full border-2 border-white shadow-md"
          />
          <h1 className="text-white font-bold text-xl tracking-wide">
            Velo Repairs
          </h1>
        </div>

        {/* Navigation Links */}
        <div className="hidden md:flex items-center gap-10 font-medium">
          <Link to="/" className={isActive("/")}>
            Home
          </Link>
          <Link to="/products" className={isActive("/products")}>
            Products
          </Link>
          <Link to="/jobform" className={isActive("/jobform")}>
            Repair
          </Link>
          <Link to="/about" className={isActive("/about")}>
            About Us
          </Link>
        </div>

        {/* Mobile Menu Placeholder (optional) */}
        <div className="md:hidden">
          <button className="text-white text-2xl">â˜°</button>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
