import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import mobileImg from "../../Assets/mobile.jpg";
import laptopImg from "../../Assets/laptop.jpeg";
import speakerImg from "../../Assets/speaker.jpeg";

function Landing() {
  const navigate = useNavigate();
  const [role, setRole] = useState(localStorage.getItem("role") || "");

  // 🧩 Temporary test role buttons
  const setAdmin = () => {
    localStorage.setItem("role", "admin");
    alert("Role set to ADMIN");
    setRole("admin");
  };

  const setCustomer = () => {
    localStorage.setItem("role", "customer");
    alert("Role set to CUSTOMER");
    setRole("customer");
  };

  const clearRole = () => {
    localStorage.removeItem("role");
    alert("Role cleared");
    setRole("");
  };

  // 🖼 Carousel slides
  const slides = [
    {
      image: mobileImg,
      title: "Mobile Repairs",
      text: "Screen, battery, charging issues fixed quickly.",
    },
    {
      image: laptopImg,
      title: "Laptop & Desktop Repairs",
      text: "Hardware/software troubleshooting for PCs & laptops.",
    },
    {
      image: speakerImg,
      title: "Speaker Repairs",
      text: "Sound issues and wiring problems fixed professionally.",
    },
  ];

  const [current, setCurrent] = useState(0);

  // Auto-slide every 4 seconds
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % slides.length);
    }, 4000);
    return () => clearInterval(timer);
  }, [slides.length]);

  // Navigation
  const prevSlide = () =>
    setCurrent((prev) => (prev === 0 ? slides.length - 1 : prev - 1));
  const nextSlide = () => setCurrent((prev) => (prev + 1) % slides.length);

  // ⭐ Star rating
  const StarRating = ({ stars }) => (
    <div className="text-[#FFA500] text-lg">
      {"★".repeat(stars)}
      {"☆".repeat(5 - stars)}
    </div>
  );

  // 💬 Customer reviews
  const [reviews, setReviews] = useState([
    {
      name: "Sarah M.",
      text: "My phone screen was replaced within an hour. Excellent service!",
      stars: 5,
    },
    {
      name: "John D.",
      text: "Got my laptop fixed quickly. Works like new again!",
      stars: 4,
    },
    {
      name: "Emily R.",
      text: "They fixed my speaker perfectly. Sound is crystal clear!",
      stars: 5,
    },
  ]);

  const [formData, setFormData] = useState({ name: "", text: "", stars: 5 });

  const handleInputChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleStarClick = (stars) => setFormData({ ...formData, stars });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.name || !formData.text) return;
    const newReviews = [...reviews, formData];
    if (newReviews.length > 3) newReviews.shift();
    setReviews(newReviews);
    setFormData({ name: "", text: "", stars: 5 });
  };

  // Navigation buttons
  const handleBookRepair = () => navigate("/jobform");
  const handleCheckStatus = () => navigate("/check-status");
  const handleAddTechnician = () => navigate("/add-technician");
  const handleViewTechnicians = () => navigate("/view-technicians");

  return (
    <div className="bg-[#F8F9FA] min-h-screen font-sans">
      {/* 🧭 TEMP role buttons for testing */}
      <div className="flex justify-center gap-3 py-4">
        <button
          onClick={setAdmin}
          className="bg-[#0057B8] text-white px-3 py-1 rounded"
        >
          Set Admin
        </button>
        <button
          onClick={setCustomer}
          className="bg-[#FFA500] text-black px-3 py-1 rounded"
        >
          Set Customer
        </button>
        <button
          onClick={clearRole}
          className="bg-gray-400 text-white px-3 py-1 rounded"
        >
          Clear Role
        </button>
      </div>

      <div className="text-center mb-6">
        <h2 className="text-xl font-semibold text-[#0057B8]">
          Current Role: {role || "Not Set"}
        </h2>
      </div>

      {/* 🖼 Carousel Section */}
      <div className="relative w-full max-w-5xl mx-auto mt-6 rounded-2xl overflow-hidden shadow-xl h-[400px] bg-gray-200">
        {slides.map((slide, idx) => (
          <div
            key={idx}
            className={`absolute inset-0 transition-opacity duration-1000 ease-in-out ${
              idx === current ? "opacity-100 z-10" : "opacity-0 z-0"
            }`}
          >
            <img
              src={slide.image}
              alt={slide.title}
              className="w-full h-full object-cover"
            />
            <div className="absolute bottom-0 left-0 w-full bg-[#0057B8]/70 text-white text-center py-4">
              <h3 className="text-xl font-semibold">{slide.title}</h3>
              <p className="text-sm">{slide.text}</p>
            </div>
          </div>
        ))}

        {/* Arrows */}
        <button
          onClick={prevSlide}
          className="absolute left-4 top-1/2 -translate-y-1/2 bg-white/80 hover:bg-white text-[#0057B8] p-2 rounded-full shadow-lg transition"
        >
          &#10094;
        </button>
        <button
          onClick={nextSlide}
          className="absolute right-4 top-1/2 -translate-y-1/2 bg-white/80 hover:bg-white text-[#0057B8] p-2 rounded-full shadow-lg transition"
        >
          &#10095;
        </button>

        {/* Dots */}
        <div className="absolute bottom-5 left-0 right-0 flex justify-center gap-2">
          {slides.map((_, idx) => (
            <button
              key={idx}
              onClick={() => setCurrent(idx)}
              className={`w-3 h-3 rounded-full transition-all ${
                idx === current ? "bg-[#FFA500]" : "bg-white/50"
              }`}
            ></button>
          ))}
        </div>
      </div>

      {/* ⚙️ Buttons Section */}
      <div className="text-center mt-10 flex flex-wrap justify-center gap-6">
        {/* Customer buttons */}
        {role === "customer" && (
          <>
            <button
              onClick={handleBookRepair}
              className="bg-[#FFA500] text-[#212529] font-semibold px-8 py-3 rounded-full hover:bg-[#ffb733] transition"
            >
              🧾 Make a Repair
            </button>

            <button
              onClick={handleCheckStatus}
              className="bg-[#0057B8] text-white font-semibold px-8 py-3 rounded-full hover:bg-[#00489a] transition"
            >
              🔍 Check Status / Modify
            </button>
          </>
        )}

        {/* Admin buttons */}
        {role === "admin" && (
          <>
            <button
              onClick={handleAddTechnician}
              className="bg-white text-[#0057B8] border-2 border-[#0057B8] font-semibold px-8 py-3 rounded-full hover:bg-[#0057B8] hover:text-white transition"
            >
              ➕ Add Technician
            </button>

            <button
              onClick={handleViewTechnicians}
              className="bg-green-600 text-white font-semibold px-8 py-3 rounded-full hover:bg-green-700 transition"
            >
              👀 View Technicians
            </button>
          </>
        )}
      </div>

      {/* 💬 Reviews Section */}
      <div className="bg-white py-16 mt-16">
        <div className="max-w-6xl mx-auto px-6">
          <h2 className="text-center text-3xl font-bold text-[#0057B8] mb-12">
            What Our Customers Say
          </h2>

          {/* Review Cards */}
          <div className="grid md:grid-cols-3 gap-8">
            {reviews.map((review, idx) => (
              <div
                key={idx}
                className="bg-gray-100 p-6 rounded-2xl shadow hover:-translate-y-1 transition"
              >
                <StarRating stars={review.stars} />
                <p className="text-gray-600 mt-3">"{review.text}"</p>
                <h6 className="font-bold text-gray-900 mt-4">
                  - {review.name}
                </h6>
              </div>
            ))}
          </div>

          {/* Add Review Form */}
          <div className="mt-16 max-w-lg mx-auto">
            <h4 className="text-center text-xl font-semibold mb-6">
              Add Your Review
            </h4>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block mb-1 font-medium">Your Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="Enter your name"
                  className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-[#0057B8]"
                  required
                />
              </div>
              <div>
                <label className="block mb-1 font-medium">Your Review</label>
                <textarea
                  name="text"
                  rows="3"
                  value={formData.text}
                  onChange={handleInputChange}
                  placeholder="Write your review"
                  className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-[#0057B8]"
                  required
                ></textarea>
              </div>
              <div className="text-center">
                <label className="block mb-1 font-medium">Rating</label>
                <div className="text-2xl cursor-pointer">
                  {[1, 2, 3, 4, 5].map((star) => (
                    <span
                      key={star}
                      onClick={() => handleStarClick(star)}
                      className={`${
                        formData.stars >= star
                          ? "text-[#FFA500]"
                          : "text-gray-300"
                      } mx-1`}
                    >
                      ★
                    </span>
                  ))}
                </div>
              </div>
              <div className="text-center">
                <button
                  type="submit"
                  className="bg-[#0057B8] text-white px-8 py-2 rounded-full font-semibold hover:bg-[#00489a] transition"
                >
                  Submit Review
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Landing;
