import React, { useState } from "react";
import { Container, Form, Button, Card, Alert } from "react-bootstrap";

function CheckStatus() {
  const [formData, setFormData] = useState({ jobNo: "", nic: "" });
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setResult(null);

    try {
      const query = formData.jobNo
        ? `jobNo=${formData.jobNo}`
        : `nic=${formData.nic}`;

      const res = await fetch(`http://localhost:5000/techs/status?${query}`);
      const data = await res.json();

      if (!res.ok) {
        setError(data.message || "No job found");
      } else {
        setResult(data);
      }
    } catch (err) {
      console.error(err);
      setError("Server error. Please try again later.");
    }
  };

  return (
    <div style={{ backgroundColor: "#F8F9FA", minHeight: "100vh", padding: "60px 0" }}>
      <Container style={{ maxWidth: "600px" }}>
        <Card
          style={{
            padding: "30px",
            borderRadius: "15px",
            boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
          }}
        >
          <h2 className="text-center mb-4" style={{ color: "#0057B8", fontWeight: "700" }}>
            Check Repair Status
          </h2>

          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Job Number</Form.Label>
              <Form.Control
                type="text"
                name="jobNo"
                value={formData.jobNo}
                onChange={handleChange}
                placeholder="Enter your Job Number"
              />
            </Form.Group>

            <div className="text-center mb-3">— OR —</div>

            <Form.Group className="mb-4">
              <Form.Label>NIC Number</Form.Label>
              <Form.Control
                type="text"
                name="nic"
                value={formData.nic}
                onChange={handleChange}
                placeholder="Enter your NIC Number"
              />
            </Form.Group>

            <div className="text-center">
              <Button
                type="submit"
                style={{
                  backgroundColor: "#FFA500",
                  color: "#212529",
                  fontWeight: "600",
                  padding: "10px 30px",
                  borderRadius: "50px",
                  border: "none",
                }}
              >
                Check Status
              </Button>
            </div>
          </Form>

          {/* Results */}
          {error && <Alert variant="danger" className="mt-4">{error}</Alert>}
          {result && (
            <Card className="mt-4 p-3 border-0 shadow-sm">
              <h5>Job Details</h5>
              <p><strong>Name:</strong> {result.Name}</p>
              <p><strong>Email:</strong> {result.Email}</p>
              <p><strong>NIC:</strong> {result.NIC}</p>
              <p><strong>Technician:</strong> {result.Technician}</p>
              <p><strong>Mobile:</strong> {result.Mobile}</p>
              <p><strong>Description:</strong> {result.Repair_Description}</p>
              <p><strong>Status:</strong> 
                <span className="badge bg-warning text-dark ms-2">{result.Status}</span>
              </p>
            </Card>
          )}
        </Card>
      </Container>
    </div>
  );
}

export default CheckStatus;
