import React, { useState } from "react";
import velogo from "../../Assets/velogo.jpg";

function JobForm() {
  const [formData, setFormData] = useState({
    Name: "",
    Email: "",
    NIC: "",
    Technician: "",
    Mobile: "",
    Repair_Description: "",
    Status: "Pending",
  });

  const [errors, setErrors] = useState({});
  const [success, setSuccess] = useState("");
  const [jobNo, setJobNo] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validation
  const validate = () => {
    const newErrors = {};
    if (!formData.Name.trim()) newErrors.Name = "Customer name is required";
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.Email))
      newErrors.Email = "Invalid email format";
    if (!/^[0-9Vv]{10,12}$/.test(formData.NIC))
      newErrors.NIC = "NIC must be 10–12 characters";
    if (!formData.Technician.trim())
      newErrors.Technician = "Technician is required";
    if (!/^07\d{8}$/.test(formData.Mobile))
      newErrors.Mobile = "Mobile must start with 07 and contain 10 digits";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Submit form
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;
    try {
      const response = await fetch("http://localhost:5000/techs", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const result = await response.json();
      if (response.ok) {
        setSuccess("✅ Job card created successfully!");
        setJobNo(result.Job_No);
        setFormData({
          Name: "",
          Email: "",
          NIC: "",
          Technician: "",
          Mobile: "",
          Repair_Description: "",
          Status: "Pending",
        });
        setErrors({});
        setTimeout(() => setSuccess(""), 4000);
      } else {
        alert(result.message || "Failed to create job card");
      }
    } catch (error) {
      console.error(error);
      alert("Error submitting form");
    }
  };

  const downloadPDF = async () => {
    if (!jobNo) return alert("No job created yet!");
    const response = await fetch(`http://localhost:5000/techs/${jobNo}/pdf`);
    if (!response.ok) return alert("Failed to download PDF");
    const blob = await response.blob();
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `JobCard_${jobNo}.pdf`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#F8F9FA] via-white to-[#E6ECFF] py-16 font-sans">
      <div className="max-w-4xl mx-auto backdrop-blur-lg bg-white/60 shadow-2xl rounded-3xl p-10 border border-white/40 relative overflow-hidden">
        <div className="absolute inset-0 rounded-3xl bg-gradient-to-tr from-[#0057B8]/20 via-transparent to-[#FFA500]/20 pointer-events-none"></div>

        {/* Header */}
        <div className="flex flex-col items-center mb-10 relative z-10">
          <img
            src={velogo}
            alt="Logo"
            className="w-24 h-24 rounded-full border-4 border-white shadow-md mb-3"
          />
          <h2 className="text-3xl font-bold text-[#0057B8] drop-shadow-sm">
            🧾 Customer Job Card
          </h2>
          <p className="text-[#0057B8]/70 mt-1">
            Create and manage repair requests efficiently
          </p>
        </div>

        {/* Form Section */}
        <div className="relative z-10">
          {success && (
            <div className="bg-green-50 border-l-4 border-green-500 text-green-700 p-4 mb-6 rounded animate-fade-in">
              {success}
            </div>
          )}

          <form
            onSubmit={handleSubmit}
            className="grid md:grid-cols-2 gap-6 animate-fade-in"
          >
            {/* Name */}
            <div>
              <label className="font-semibold text-[#0057B8]">
                👤 Customer Name
              </label>
              <input
                type="text"
                name="Name"
                value={formData.Name}
                onChange={handleChange}
                placeholder="Enter customer name"
                className={`w-full mt-1 border ${
                  errors.Name ? "border-red-500" : "border-gray-300"
                } rounded-lg p-2 focus:ring-2 focus:ring-[#0057B8] bg-white/80`}
              />
              {errors.Name && (
                <p className="text-red-500 text-sm">{errors.Name}</p>
              )}
            </div>

            {/* Email */}
            <div>
              <label className="font-semibold text-[#0057B8]">
                ✉️ Email Address
              </label>
              <input
                type="email"
                name="Email"
                value={formData.Email}
                onChange={handleChange}
                placeholder="Enter email address"
                className={`w-full mt-1 border ${
                  errors.Email ? "border-red-500" : "border-gray-300"
                } rounded-lg p-2 focus:ring-2 focus:ring-[#0057B8] bg-white/80`}
              />
              {errors.Email && (
                <p className="text-red-500 text-sm">{errors.Email}</p>
              )}
            </div>

            {/* NIC */}
            <div>
              <label className="font-semibold text-[#0057B8]">
                🆔 National ID (NIC)
              </label>
              <input
                type="text"
                name="NIC"
                value={formData.NIC}
                onChange={handleChange}
                placeholder="Enter NIC number"
                className={`w-full mt-1 border ${
                  errors.NIC ? "border-red-500" : "border-gray-300"
                } rounded-lg p-2 focus:ring-2 focus:ring-[#0057B8] bg-white/80`}
              />
              {errors.NIC && (
                <p className="text-red-500 text-sm">{errors.NIC}</p>
              )}
            </div>

            {/* Mobile */}
            <div>
              <label className="font-semibold text-[#0057B8]">
                📱 Mobile Number
              </label>
              <div className="flex mt-1">
                <span className="bg-gray-200 border border-r-0 border-gray-300 px-3 py-2 rounded-l-lg text-gray-700">
                  +94
                </span>
                <input
                  type="text"
                  name="Mobile"
                  value={formData.Mobile}
                  onChange={handleChange}
                  placeholder="07XXXXXXXX"
                  className={`w-full border ${
                    errors.Mobile ? "border-red-500" : "border-gray-300"
                  } rounded-r-lg p-2 focus:ring-2 focus:ring-[#0057B8] bg-white/80`}
                />
              </div>
              {errors.Mobile && (
                <p className="text-red-500 text-sm">{errors.Mobile}</p>
              )}
            </div>

            {/* Technician */}
            <div>
              <label className="font-semibold text-[#0057B8]">
                🔧 Assigned Technician
              </label>
              <input
                type="text"
                name="Technician"
                value={formData.Technician}
                onChange={handleChange}
                placeholder="Enter technician name"
                className={`w-full mt-1 border ${
                  errors.Technician ? "border-red-500" : "border-gray-300"
                } rounded-lg p-2 focus:ring-2 focus:ring-[#0057B8] bg-white/80`}
              />
              {errors.Technician && (
                <p className="text-red-500 text-sm">{errors.Technician}</p>
              )}
            </div>

            {/* Status */}
            <div>
              <label className="font-semibold text-[#0057B8]">
                🏷️ Current Status
              </label>
              <div className="mt-2">
                <span className="bg-[#FFA500] text-[#212529] px-3 py-1 rounded-md text-sm font-semibold shadow-sm">
                  {formData.Status}
                </span>
              </div>
            </div>

            {/* Repair Description */}
            <div className="md:col-span-2">
              <label className="font-semibold text-[#0057B8]">
                🛠️ Repair Description
              </label>
              <textarea
                name="Repair_Description"
                value={formData.Repair_Description}
                onChange={handleChange}
                rows="4"
                placeholder="Describe the repair work needed..."
                className="w-full mt-1 border border-gray-300 rounded-lg p-2 focus:ring-2 focus:ring-[#0057B8] bg-white/80"
              ></textarea>
            </div>

            {/* Submit */}
            <div className="md:col-span-2 flex justify-center">
              <button
                type="submit"
                className="bg-[#FFA500] text-[#212529] font-bold px-8 py-3 rounded-full hover:bg-[#ffb733] hover:shadow-[#FFA500]/40 shadow-lg flex items-center gap-2 transition-transform hover:-translate-y-0.5"
              >
                <img src={velogo} alt="Logo" className="w-6 h-6" />
                Submit Job Card
              </button>
            </div>
          </form>

          {jobNo && (
            <div className="text-center mt-8 animate-slide-up">
              <button
                onClick={downloadPDF}
                className="bg-[#0057B8] text-white px-6 py-2 rounded-full hover:bg-[#00489a] shadow-lg transition-transform hover:-translate-y-0.5"
              >
                📄 Download Job Card (PDF) — Job No {jobNo}
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default JobForm;
