    <div style={{ textAlign: "center", marginTop: "50px", fontFamily: "Poppins" }}>
      <h2>üîê Role Access Test</h2>
      <p>Current role: <strong>{role || "No role found"}</strong></p>

      {role === "admin" ? (
        <div style={{ color: "green" }}>
          ‚úÖ You are an Admin! You can manage technicians, update jobs, and view all data.
        </div>
      ) : role === "customer" ? (
        <div style={{ color: "blue" }}>
          üë§ You are a Customer! You can check your job status and submit repair forms.
        </div>
      ) : (
        <div style={{ color: "red" }}>
          ‚ö†Ô∏è No role detected. Please log in or set your role manually.
        </div>
      )}
    </div>
  );
}

export default TestRoleAccess;
