import React, { useState } from "react";
import {
  Container,
  Form,
  Button,
  Card,
  Alert,
  OverlayTrigger,
  Tooltip,
  Modal,
} from "react-bootstrap";
import { FaEdit, FaTrash } from "react-icons/fa";

function CheckStatus() {
  const [formData, setFormData] = useState({ jobNo: "", nic: "" });
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const [showEdit, setShowEdit] = useState(false);
  const [editData, setEditData] = useState({
    Name: "",
    Email: "",
    NIC: "",
    Technician: "",
    Mobile: "",
    Repair_Description: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setResult(null);

    try {
      const query = formData.jobNo
        ? `jobNo=${formData.jobNo}`
        : `nic=${formData.nic}`;

      const res = await fetch(`http://localhost:5000/techs/status?${query}`);
      const data = await res.json();

      if (!res.ok) {
        setError(data.message || "No job found");
      } else {
        setResult(data);
      }
    } catch (err) {
      console.error(err);
      setError("Server error. Please try again later.");
    }
  };

  const handleEdit = () => {
    if (!result) return;
    setEditData({
      Name: result.Name || "",
      Email: result.Email || "",
      NIC: result.NIC || "",
      Technician: result.Technician || "",
      Mobile: result.Mobile || "",
      Repair_Description: result.Repair_Description || "",
    });
    setShowEdit(true);
  };

  const saveEdit = async () => {
    if (!result || !result._id) {
      alert("Job ID not found.");
      return;
    }

    try {
      const res = await fetch(`http://localhost:5000/techs/${result._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editData),
      });

      if (res.ok) {
        const updated = await res.json();
        setResult(updated);
        setShowEdit(false);
      } else {
        alert("Failed to update job");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating job");
    }
  };
const handleDelete = async () => {
  if (!result?.jobNo) {
    alert("Job Number not found.");
    return;
  }

  if (!window.confirm("Are you sure you want to delete this job?")) return;

  try {
    const res = await fetch(`http://localhost:5000/techs/job/${result.jobNo}`, {
      method: "DELETE",
    });

    if (res.ok) {
      alert("Job deleted successfully!");
      setResult(null);
    } else {
      const data = await res.json();
      alert(data.message || "Failed to delete job.");
    }
  } catch (error) {
    console.error(error);
    alert("Error deleting job.");
  }
};


  return (
    <div style={{ backgroundColor: "#F8F9FA", minHeight: "100vh", padding: "60px 0" }}>
      <Container style={{ maxWidth: "600px" }}>
        <Card style={{ padding: "30px", borderRadius: "15px", boxShadow: "0 4px 10px rgba(0,0,0,0.1)" }}>
          <h2 className="text-center mb-4" style={{ color: "#0057B8", fontWeight: "700" }}>
            Check Repair Status
          </h2>

          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Job Number</Form.Label>
              <Form.Control
                type="text"
                name="jobNo"
                value={formData.jobNo}
                onChange={handleChange}
                placeholder="Enter your Job Number"
              />
            </Form.Group>

            <div className="text-center mb-3">— OR —</div>

            <Form.Group className="mb-4">
              <Form.Label>NIC Number</Form.Label>
              <Form.Control
                type="text"
                name="nic"
                value={formData.nic}
                onChange={handleChange}
                placeholder="Enter your NIC Number"
              />
            </Form.Group>

            <div className="text-center">
              <Button
                type="submit"
                style={{
                  backgroundColor: "#FFA500",
                  color: "#212529",
                  fontWeight: "600",
                  padding: "10px 30px",
                  borderRadius: "50px",
                  border: "none",
                }}
              >
                Check Status
              </Button>
            </div>
          </Form>

          {error && <Alert variant="danger" className="mt-4">{error}</Alert>}

          {result && (
            <Card className="mt-4 p-3 border-0 shadow-sm">
              <div className="d-flex justify-content-between align-items-center">
                <h5>Job Details</h5>
                <div>
                  <OverlayTrigger placement="top" overlay={<Tooltip>Update</Tooltip>}>
                    <Button variant="outline-primary" size="sm" className="me-2" onClick={handleEdit}>
                      <FaEdit />
                    </Button>
                  </OverlayTrigger>
                  <OverlayTrigger placement="top" overlay={<Tooltip>Delete</Tooltip>}>
                    <Button variant="outline-danger" size="sm" onClick={handleDelete}>
                      <FaTrash />
                    </Button>
                  </OverlayTrigger>
                </div>
              </div>

              <p><strong>Name:</strong> {result.Name}</p>
              <p><strong>Email:</strong> {result.Email}</p>
              <p><strong>NIC:</strong> {result.NIC}</p>
              <p><strong>Technician:</strong> {result.Technician}</p>
              <p><strong>Mobile:</strong> {result.Mobile}</p>
              <p><strong>Description:</strong> {result.Repair_Description}</p>
              <p>
                <strong>Status:</strong>
                <span className="badge bg-warning text-dark ms-2">{result?.status || "Pending"}</span>
              </p>
            </Card>
          )}
        </Card>
      </Container>

      {/* Edit Modal */}
      <Modal show={showEdit} onHide={() => setShowEdit(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Job Details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={editData.Name}
                onChange={(e) => setEditData({ ...editData, Name: e.target.value })}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                value={editData.Email}
                onChange={(e) => setEditData({ ...editData, Email: e.target.value })}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>NIC</Form.Label>
              <Form.Control
                type="text"
                value={editData.NIC}
                onChange={(e) => setEditData({ ...editData, NIC: e.target.value })}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Technician</Form.Label>
              <Form.Control
                type="text"
                value={editData.Technician}
                onChange={(e) => setEditData({ ...editData, Technician: e.target.value })}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Mobile</Form.Label>
              <Form.Control
                type="text"
                value={editData.Mobile}
                onChange={(e) => setEditData({ ...editData, Mobile: e.target.value })}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={editData.Repair_Description}
                onChange={(e) => setEditData({ ...editData, Repair_Description: e.target.value })}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Status</Form.Label>
              <div>
                <span className="badge bg-warning text-dark">{result?.status || "Pending"}</span>
              </div>
            </Form.Group>
          </Form>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEdit(false)}>Cancel</Button>
          <Button
            style={{ backgroundColor: "#FFA500", color: "#212529", border: "none", fontWeight: "600" }}
            onClick={saveEdit}
          >
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default CheckStatus;
