import React, { useState } from "react";
import { Container, Card, Form, Button, Alert, Badge } from "react-bootstrap";
import CustomerHeader from "../Nav/CustomerHeader";

function JobForm() {
  const [formData, setFormData] = useState({
    Name: "",
    Email: "",
    NIC: "",
    Technician: "",
    Mobile: "",
    Repair_Description: "",
    Status: "Pending",
  });

  const [errors, setErrors] = useState({});
  const [success, setSuccess] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const validate = () => {
    let newErrors = {};
    if (!formData.Name.trim()) newErrors.Name = "Customer name is required";
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.Email))
      newErrors.Email = "Invalid email format";
    if (!/^\d{10,12}$/.test(formData.NIC))
      newErrors.NIC = "NIC must be 10â€“12 digits only";
    if (!formData.Technician.trim())
      newErrors.Technician = "Technician is required";
    if (!/^07\d{8}$/.test(formData.Mobile))
      newErrors.Mobile = "Mobile must start with 07 and contain 10 digits";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;
    try {
      const response = await fetch("http://localhost:5000/techs", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const result = await response.json();
      if (response.ok) {
        setSuccess("Job card created successfully!");
        setFormData({
          Name: "",
          Email: "",
          NIC: "",
          Technician: "",
          Mobile: "",
          Repair_Description: "",
          Status: "Pending",
        });
        setErrors({});
      } else {
        alert(result.message || "Failed to create job card");
      }
    } catch (error) {
      console.error(error);
      alert("Error submitting form");
    }
  };

  const getStatusVariant = (status) => {
    switch (status.toLowerCase()) {
      case "completed":
        return "success"; // Green
      case "pending":
      case "in-progress":
        return "warning"; // Amber
      case "failed":
        return "danger"; // Red
      default:
        return "secondary";
    }
  };

  return (
    <>
      {/* Navbar */}
      <CustomerHeader />

      {/* Add padding-top to avoid form being hidden behind fixed navbar */}
      <div style={{ paddingTop: "100px", backgroundColor: "#F8F9FA", minHeight: "100vh" }}>
        <Container>
          <Card className="shadow border-0">
            <Card.Header
              style={{ backgroundColor: "#0057B8", color: "#FFFFFF", fontWeight: "bold" }}
            >
              Customer Job Card
            </Card.Header>
            <Card.Body>
              {success && <Alert variant="success">{success}</Alert>}
              <Form onSubmit={handleSubmit}>
                {/* Name */}
                <Form.Group className="mb-3" controlId="formName">
                  <Form.Label style={{ color: "#212529", fontWeight: "600" }}>Customer Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="Name"
                    value={formData.Name}
                    onChange={handleChange}
                    isInvalid={!!errors.Name}
                  />
                  <Form.Control.Feedback type="invalid">{errors.Name}</Form.Control.Feedback>
                </Form.Group>

                {/* Email */}
                <Form.Group className="mb-3" controlId="formEmail">
                  <Form.Label style={{ color: "#212529", fontWeight: "600" }}>Email</Form.Label>
                  <Form.Control
                    type="email"
                    name="Email"
                    value={formData.Email}
                    onChange={handleChange}
                    isInvalid={!!errors.Email}
                  />
                  <Form.Control.Feedback type="invalid">{errors.Email}</Form.Control.Feedback>
                </Form.Group>

                {/* NIC */}
                <Form.Group className="mb-3" controlId="formNIC">
                  <Form.Label style={{ color: "#212529", fontWeight: "600" }}>NIC</Form.Label>
                  <Form.Control
                    type="text"
                    name="NIC"
                    value={formData.NIC}
                    onChange={handleChange}
                    minLength="10"
                    maxLength="12"
                    isInvalid={!!errors.NIC}
                  />
                  <Form.Control.Feedback type="invalid">{errors.NIC}</Form.Control.Feedback>
                </Form.Group>

                {/* Technician */}
                <Form.Group className="mb-3" controlId="formTechnician">
                  <Form.Label style={{ color: "#212529", fontWeight: "600" }}>Technician Assigned</Form.Label>
                  <Form.Control
                    type="text"
                    name="Technician"
                    value={formData.Technician}
                    onChange={handleChange}
                    isInvalid={!!errors.Technician}
                  />
                  <Form.Control.Feedback type="invalid">{errors.Technician}</Form.Control.Feedback>
                </Form.Group>

                {/* Mobile */}
                <Form.Group className="mb-3" controlId="formMobile">
                  <Form.Label style={{ color: "#212529", fontWeight: "600" }}>Mobile Number</Form.Label>
                  <Form.Control
                    type="text"
                    name="Mobile"
                    value={formData.Mobile}
                    onChange={handleChange}
                    placeholder="07XXXXXXXX"
                    minLength="10"
                    maxLength="10"
                    isInvalid={!!errors.Mobile}
                  />
                  <Form.Control.Feedback type="invalid">{errors.Mobile}</Form.Control.Feedback>
                </Form.Group>

                {/* Repair Description */}
                <Form.Group className="mb-3" controlId="formDescription">
                  <Form.Label style={{ color: "#212529", fontWeight: "600" }}>Repair Description</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name="Repair_Description"
                    value={formData.Repair_Description}
                    onChange={handleChange}
                  />
                </Form.Group>

                {/* Status */}
                <Form.Group className="mb-3" controlId="formStatus">
                  <Form.Label style={{ color: "#212529", fontWeight: "600" }}>Status</Form.Label>
                  <div>
                    <Badge bg={getStatusVariant(formData.Status)}>{formData.Status}</Badge>
                  </div>
                </Form.Group>

                {/* Submit */}
                <Button
                  type="submit"
                  className="w-100"
                  style={{
                    backgroundColor: "#FFA500",
                    borderColor: "#FFA500",
                    fontWeight: "600",
                  }}
                >
                  Submit Job Card
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Container>
      </div>
    </>
  );
}

export default JobForm;
