import React from "react";
import { Link, useLocation } from "react-router-dom";
import velogo from "../../Assets/velogo.jpg";

function Navbar() {
  const location = useLocation();

  const isActive = (path) =>
    location.pathname === path
      ? "text-[#FFA500] border-b-2 border-[#FFA500] pb-1"
      : "text-white hover:text-[#FFA500] transition duration-300";

  return (
    <nav className="bg-gradient-to-r from-[#0057B8] via-[#0074D9] to-[#0057B8] shadow-xl fixed top-0 left-0 w-full z-50">
      <div className="max-w-7xl mx-auto px-6 py-3 flex justify-between items-center">
        {/*  Brand */}
        <div className="flex items-center gap-3 cursor-pointer">
          <img
            src={velogo}
            alt="Logo"
            className="w-11 h-11 rounded-full border-2 border-white shadow-md hover:scale-105 transition-transform"
          />
          <h1 className="text-white font-extrabold text-xl tracking-wide drop-shadow-sm">
            Vijaya<span className="text-[#FFA500]">Electricals</span>
          </h1>
        </div>

        {/* Navigation Links */}
        <div className="hidden md:flex items-center gap-10 font-medium text-lg">
          <Link to="/home" className={isActive("/home")}>
            Home
          </Link>
          <Link to="/products" className={isActive("/products")}>
            Products
          </Link>
          <Link to="/landing" className={isActive("/landing")}>
            Repairs
          </Link>
          <Link to="/about" className={isActive("/about")}>
            About Us
          </Link>
        </div>

        {/* Mobile Menu Icon */}
        <div className="md:hidden">
          <button className="text-white text-3xl hover:text-[#FFA500] transition">
            â˜°
          </button>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
