import React, { useState, useEffect } from "react";

function AddTechnician() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    mobile: "",
    specialization: "",
  });
  const [technicians, setTechnicians] = useState([]);
  const [editId, setEditId] = useState(null);
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");
  const [showList, setShowList] = useState(false); // üëà Toggle list visibility

  // ‚úÖ Fetch all technicians
  const fetchTechnicians = async () => {
    try {
      const res = await fetch("http://localhost:5000/technicians");
      const data = await res.json();
      setTechnicians(data);
    } catch (err) {
      console.error(err);
      setError("Error fetching technicians");
    }
  };

  // ‚úÖ Auto-refresh when list is shown
  useEffect(() => {
    if (showList) fetchTechnicians();
  }, [showList]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ‚úÖ Handle Add / Update
  const handleSubmit = async (e) => {
    e.preventDefault();
    setSuccess("");
    setError("");

    const method = editId ? "PUT" : "POST";
    const url = editId
      ? `http://localhost:5000/technicians/${editId}`
      : "http://localhost:5000/technicians";

    try {
      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const result = await res.json();

      if (res.ok) {
        setSuccess(editId ? "‚úèÔ∏è Technician updated!" : "‚úÖ Technician added!");
        setFormData({ name: "", email: "", mobile: "", specialization: "" });
        setEditId(null);
        if (showList) fetchTechnicians();
      } else {
        setError(result.message || "Failed to process technician");
      }
    } catch (err) {
      console.error(err);
      setError("Error connecting to server");
    }
  };

  const handleEdit = (tech) => {
    setEditId(tech._id);
    setFormData({
      name: tech.name,
      email: tech.email,
      mobile: tech.mobile,
      specialization: tech.specialization,
    });
    setSuccess("");
    setError("");
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this technician?")) return;
    try {
      const res = await fetch(`http://localhost:5000/technicians/${id}`, {
        method: "DELETE",
      });
      if (res.ok) {
        setSuccess("üóëÔ∏è Technician deleted successfully!");
        fetchTechnicians();
      } else {
        setError("Failed to delete technician");
      }
    } catch (err) {
      console.error(err);
      setError("Error deleting technician");
    }
  };

  return (
    <div className="min-h-screen bg-[#F8F9FA] flex flex-col items-center py-10 font-sans">
      {/* üßæ Add / Edit Technician Form */}
      <div className="bg-white shadow-lg rounded-2xl p-10 w-full max-w-md border border-gray-200 mb-10">
        <h2 className="text-2xl font-bold text-[#0057B8] mb-6 text-center">
          {editId ? "‚úèÔ∏è Edit Technician" : "‚ûï Add New Technician"}
        </h2>

        {success && <p className="text-green-600 text-center mb-4">{success}</p>}
        {error && <p className="text-red-600 text-center mb-4">{error}</p>}

        <form onSubmit={handleSubmit} className="flex flex-col gap-4">
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Technician Name"
            className="border p-3 rounded-lg focus:ring-2 focus:ring-[#0057B8]"
            required
          />
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Email Address"
            className="border p-3 rounded-lg focus:ring-2 focus:ring-[#0057B8]"
          />
          <input
            type="text"
            name="mobile"
            value={formData.mobile}
            onChange={handleChange}
            placeholder="Mobile Number (07XXXXXXXX)"
            className="border p-3 rounded-lg focus:ring-2 focus:ring-[#0057B8]"
            required
          />
          <input
            type="text"
            name="specialization"
            value={formData.specialization}
            onChange={handleChange}
            placeholder="Specialization (e.g. Laptop Repairs)"
            className="border p-3 rounded-lg focus:ring-2 focus:ring-[#0057B8]"
          />

          <button
            type="submit"
            className="bg-[#FFA500] text-[#212529] font-semibold py-3 rounded-lg hover:bg-[#ffb733] transition"
          >
            {editId ? "Update Technician" : "Add Technician"}
          </button>
        </form>

        {/* üëÄ Toggle button */}
        <div className="text-center mt-6">
          <button
            onClick={() => setShowList(!showList)}
            className="text-[#0057B8] font-semibold hover:underline"
          >
            {showList ? "üîΩ Hide My Technicians" : "üëÄ View My Technicians"}
          </button>
        </div>
      </div>

      {/* üìã Technician List (Visible only when showList = true) */}
      {showList && (
        <div className="w-full max-w-4xl bg-white shadow-lg rounded-2xl p-6 border border-gray-200 animate-fade-in">
          <h3 className="text-xl font-bold text-[#0057B8] mb-4 text-center">
            üë∑ My Technicians
          </h3>
          {technicians.length === 0 ? (
            <p className="text-gray-500 text-center">No technicians added yet.</p>
          ) : (
            <table className="w-full border-collapse text-sm text-gray-700">
              <thead>
                <tr className="bg-[#0057B8]/10 text-[#0057B8] font-semibold">
                  <th className="p-2 border">Name</th>
                  <th className="p-2 border">Email</th>
                  <th className="p-2 border">Mobile</th>
                  <th className="p-2 border">Specialization</th>
                  <th className="p-2 border">Actions</th>
                </tr>
              </thead>
              <tbody>
                {technicians.map((tech) => (
                  <tr key={tech._id} className="hover:bg-gray-50 transition">
                    <td className="border p-2">{tech.name}</td>
                    <td className="border p-2">{tech.email}</td>
                    <td className="border p-2">{tech.mobile}</td>
                    <td className="border p-2">{tech.specialization}</td>
                    <td className="border p-2 text-center">
                      <button
                        onClick={() => handleEdit(tech)}
                        className="text-blue-600 font-semibold hover:underline mx-2"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDelete(tech._id)}
                        className="text-red-600 font-semibold hover:underline mx-2"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
}

export default AddTechnician;
