import React, { useState } from "react";
import { Navbar, Nav, Container, Badge, NavDropdown } from "react-bootstrap";
import { Bell, ShoppingCart, User } from "lucide-react";

const CustomerHeader = () => {
  const [notifications] = useState([
    { id: 1, title: "Order Confirmed", message: "Order #VE001 confirmed", type: "success" },
    { id: 2, title: "Repair Update", message: "Your repair is in progress", type: "info" },
    { id: 3, title: "Payment Reminder", message: "Payment pending for invoice #INV-123", type: "warning" },
  ]);

  return (
    <Navbar bg="primary" variant="dark" expand="lg" fixed="top">
      <Container>
        <Navbar.Brand href="/">
          Vijaya Electronics
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="/">Home</Nav.Link>
            <Nav.Link href="/products">Products</Nav.Link>
            <Nav.Link href="/repair">Repair</Nav.Link>
            <Nav.Link href="/about">About Us</Nav.Link>
          </Nav>

          <Nav className="ms-auto align-items-center">
            {/* Notifications */}
            <NavDropdown
              title={
                <div style={{ position: "relative" }}>
                  <Bell color="white" size={20} />
                  <Badge bg="warning" pill style={{ position: "absolute", top: "-5px", right: "-10px" }}>
                    {notifications.length}
                  </Badge>
                </div>
              }
              id="notifications-dropdown"
              align="end"
            >
              {notifications.map((note) => (
                <NavDropdown.Item key={note.id}>
                  <strong>{note.title}</strong>
                  <div style={{ fontSize: "0.8rem" }}>{note.message}</div>
                </NavDropdown.Item>
              ))}
            </NavDropdown>

            {/* Cart */}
            <Nav.Link href="/cart" className="position-relative mx-2">
              <ShoppingCart color="white" size={20} />
              <Badge bg="warning" pill style={{ position: "absolute", top: "-5px", right: "-10px" }}>
                2
              </Badge>
            </Nav.Link>

            {/* User */}
            <Nav.Link href="/profile">
              <User color="white" size={20} />
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default CustomerHeader;
